<?php

namespace System\Web\HTML{	class Element	{		public $Attributes;		public $CanSelfClose;		public $DataAttributes;		public $Events;		public $NodeName;		public $Style;				public $Children;				public function __construct($nodeName)		{			$this->CanSelfClose = false;			$this->NodeName = $nodeName;						$this->Attributes=array();			$this->Children=array();			$this->DataAttributes=array();			$this->Events=array();			$this->Style=array();		}				public function Load($domElement)		{		}				public function Render($level=0)		{			$indent = str_repeat("\t",$level);						$ret=$indent.'<'.$this->NodeName;						foreach( $this->Attibutes as $name => $value )			{				$ret .= ' '.$name.'="'.$value.'"';			}						foreach( $this->DataAttributes as $name => $value )			{				$ret .= ' data-'.$name.'="'.$value.'"';			}						if( count($this->Style) > 0 )			{				$ret .= ' style="';				foreach( $this->Style as $name => $value )				{					$ret .= $name.': '.$value.'; ';				}				$ret .= '"';			}						foreach( $this->Events as $name => $value )			{				$ret .= ' '.$name.'="'.$value.'"';			}						if( count($this->Children) == 0 )			{				if( $this->CanSelfClose )					$ret .= ' />';				else					$ret .= '></'.$this->NodeName.'>';			}			else			{				$ret .= '>'."\r\n";				foreach( $this->Children as $child )				{					$ret .= $child->Render($level+1);				}				$ret .=$indent.'</'.$this->NodeName.'>';			}						$ret .= "\r\n";						return $ret;		}	}}